    # Step 1: Ingest domain
    echo "   📥 Ingesting domain..."
    INGEST_RESPONSE=$(curl -s -X POST "$API_BASE/api/v1/ingest/domain" \
        -H "Content-Type: application/json" \
        -d "{\"domain\": \"$domain\"}" || echo "FAILED")
    
    if echo "$INGEST_RESPONSE" | grep -q "ingested\|already exists"; then
        echo -e "      ${GREEN}✅ Domain ingested${NC}"
    else
        echo -e "      ${YELLOW}⚠️  Ingest response: $INGEST_RESPONSE${NC}"
    fi
    
    # Step 2: Scan domain
    echo "   🔍 Scanning domain..."
    SCAN_RESPONSE=$(curl -s -X POST "$API_BASE/api/v1/scan/domain" \
        -H "Content-Type: application/json" \
        -d "{\"domain\": \"$domain\"}" || echo "FAILED")
    
    if echo "$SCAN_RESPONSE" | grep -q "scan_status.*success"; then
        echo -e "      ${GREEN}✅ Domain scanned${NC}"
    else
        echo -e "      ${RED}❌ Scan failed: $SCAN_RESPONSE${NC}"
        return 1
    fi
    
    # Wait for scan to complete
    sleep 3
    
    # Step 3: Get lead details
    echo "   📊 Getting lead details..."
    LEAD_RESPONSE=$(curl -s "$API_BASE/api/v1/leads/$domain" || echo "FAILED")
    
    if [ "$LEAD_RESPONSE" = "FAILED" ]; then
        echo -e "      ${RED}❌ Failed to get lead details${NC}"
        return 1
    fi
    
    # Extract P-Model fields
    PRIORITY_CAT=$(echo "$LEAD_RESPONSE" | grep -o '"priority_category":"[^"]*"' | cut -d'"' -f4 || echo "NOT_FOUND")
    COMM_SEGMENT=$(echo "$LEAD_RESPONSE" | grep -o '"commercial_segment":"[^"]*"' | cut -d'"' -f4 || echo "NOT_FOUND")
    TECH_HEAT=$(echo "$LEAD_RESPONSE" | grep -o '"technical_heat":"[^"]*"' | cut -d'"' -f4 || echo "NOT_FOUND")
    COMM_HEAT=$(echo "$LEAD_RESPONSE" | grep -o '"commercial_heat":"[^"]*"' | cut -d'"' -f4 || echo "NOT_FOUND")
    PRIORITY_LABEL=$(echo "$LEAD_RESPONSE" | grep -o '"priority_label":"[^"]*"' | cut -d'"' -f4 || echo "NOT_FOUND")
    
    # Verify P-Model fields
    echo "   ✅ P-Model Fields:"
    echo "      - Priority Category: $PRIORITY_CAT (expected: $expected_priority)"
    echo "      - Commercial Segment: $COMM_SEGMENT (expected: $expected_segment)"
    echo "      - Technical Heat: $TECH_HEAT (expected: $expected_heat)"
    echo "      - Commercial Heat: $COMM_HEAT"
    echo "      - Priority Label: $PRIORITY_LABEL"
    
    # Verify expectations
    if [ "$PRIORITY_CAT" = "$expected_priority" ]; then
        echo -e "      ${GREEN}✅ Priority Category matches${NC}"
    else
        echo -e "      ${YELLOW}⚠️  Priority Category mismatch (got: $PRIORITY_CAT, expected: $expected_priority)${NC}"
    fi
    
    if [ "$COMM_SEGMENT" = "$expected_segment" ]; then
        echo -e "      ${GREEN}✅ Commercial Segment matches${NC}"
    else
        echo -e "      ${YELLOW}⚠️  Commercial Segment mismatch (got: $COMM_SEGMENT, expected: $expected_segment)${NC}"
    fi
    
    if [ "$TECH_HEAT" = "$expected_heat" ]; then
        echo -e "      ${GREEN}✅ Technical Heat matches${NC}"
    else
        echo -e "      ${YELLOW}⚠️  Technical Heat mismatch (got: $TECH_HEAT, expected: $expected_heat)${NC}"
    fi
    
    # Step 4: Get score breakdown
    echo "   📈 Getting score breakdown..."
    BREAKDOWN_RESPONSE=$(curl -s "$API_BASE/api/v1/leads/$domain/score-breakdown" || echo "FAILED")
    
    if echo "$BREAKDOWN_RESPONSE" | grep -q "priority_category"; then
        echo -e "      ${GREEN}✅ Score breakdown includes P-Model fields${NC}"
    else
        echo -e "      ${YELLOW}⚠️  Score breakdown may not include P-Model fields${NC}"
    fi
    
    # Step 5: Get sales summary
    echo "   💼 Getting sales summary..."
    SALES_RESPONSE=$(curl -s "$API_BASE/api/v1/leads/$domain/sales-summary" || echo "FAILED")
    
    if echo "$SALES_RESPONSE" | grep -q "segment_explanation\|provider_reasoning\|security_reasoning"; then
        echo -e "      ${GREEN}✅ Sales summary includes v1.1 fields${NC}"
    else
        echo -e "      ${YELLOW}⚠️  Sales summary may not include v1.1 fields${NC}"
    fi
    
    echo ""
}

